using System;
using BSIT3L_Movies.Models;
using Microsoft.AspNetCore.Mvc;

namespace BSIT3L_Movies.Controllers
{
	public class MoviesController : Controller
    {
        private List<MovieViewModel> _movies;
        public MoviesController()
		{
            // Sample movie data
            _movies = new List<MovieViewModel>
            {
                new MovieViewModel { Id = 1, Name = "Your Name", Rating = "9", ReleaseYear = 2016, Genre = "Romance/Drama", Form = "Animation", Poster = "~/imgs/YourName.png", Trailer = "https://www.youtube.com/embed/xU47nhruN-Q?si=W5w0a_c5JaTXrTBW", Synopsis = "Mitsuha Miyamizu, a high school girl, yearns to live the life of a boy in the bustling city of Tokyo—a dream that stands in stark contrast to her present life in the countryside. Meanwhile in the city, Taki Tachibana lives a busy life as a high school student while juggling his part-time job and hopes for a future in architecture.\r\n\r\nOne day, Mitsuha awakens in a room that is not her own and suddenly finds herself living the dream life in Tokyo—but in Taki's body! Elsewhere, Taki finds himself living Mitsuha's life in the humble countryside. In pursuit of an answer to this strange phenomenon, they begin to search for one another.\r\n\r\nKimi no Na wa. revolves around Mitsuha and Taki's actions, which begin to have a dramatic impact on each other's lives, weaving them into a fabric held together by fate and circumstance." },
                new MovieViewModel { Id = 2, Name = "Weathering With You", Rating = "8", ReleaseYear = 2019, Genre = "Romance/Drama", Form = "Animation", Poster = "~/imgs/Weathering.png", Trailer = "https://www.youtube.com/embed/Q6iK6DjV_iE?si=qj2sbT1aHFDLp6bn", Synopsis = "Tokyo is currently experiencing rain showers that seem to disrupt the usual pace of everyone living there to no end. Amidst this seemingly eternal downpour arrives the runaway high school student Hodaka Morishima, who struggles to financially support himself—ending up with a job at a small-time publisher. At the same time, the orphaned Hina Amano also strives to find work to sustain herself and her younger brother.\r\n\r\nBoth fates intertwine when Hodaka attempts to rescue Hina from shady men, deciding to run away together. Subsequently, Hodaka discovers that Hina has a strange yet astounding power: the ability to call out the sun whenever she prays for it. With Tokyo's unusual weather in mind, Hodaka sees the potential of this ability. He suggests that Hina should become a \"sunshine girl\"—someone who will clear the sky for people when they need it the most.\r\n\r\nThings begin looking up for them at first. However, it is common knowledge that power always comes with a hefty price..."},
				new MovieViewModel { Id = 3, Name = "Suzume", Rating = "8", ReleaseYear = 2022, Genre = "Romance/Drama", Form = "Animation", Poster = "~/imgs/Suzume.png", Trailer = "https://www.youtube.com/embed/5pTcio2hTSw?si=uiepUzEnc-ZYfZcE", Synopsis = "On her way to school one day, Suzume Iwato stumbles upon Souta Munakata, a young man searching for abandoned areas. The high school girl directs Souta to a nearby ruin, but out of pure curiosity, she herself decides to head to the same destination.\r\n\r\nOnce there, Suzume discovers an isolated door with a dreamlike universe lying beyond it—a place that she can see and feel, but not enter. A strange stone rests on the ground nearby, but it turns into a cat-like creature and scurries away when Suzume lifts it. Suddenly afraid, she heads back toward her school, not realizing that her act of leaving the door open will have consequences.\r\n\r\nWith the \"keystone\" released, the evil within the other universe can now freely escape and wreak havoc throughout Japan. Intending to correct her dangerous mistake, Suzume joins Souta—whose true goal is to prevent evil from festering—in finding and locking all open doors before the country is destroyed." },
                new MovieViewModel { Id = 4, Name = "Garden of Words", Rating = "8", ReleaseYear = 2013, Genre = "Romance/Drama", Form = "Animation",  Poster = "~/imgs/GardenOfWords.png", Trailer = "https://www.youtube.com/embed/FMabhvDoolc?si=fUYei3ZN1tbQo5NJ", Synopsis = "On a rainy morning in Tokyo, Takao Akizuki, an aspiring shoemaker, decides to skip class to sketch designs in a beautiful garden. This is where he meets Yukari Yukino, a beautiful yet mysterious woman, for the very first time. Offering to make her new shoes, Takao continues to meet with Yukari throughout the rainy season, and without even realizing it, the two are able to alleviate the worries hidden in their hearts just by being with each other. However, their personal struggles have not disappeared completely, and as the end of the rainy season approaches, their relationship will be put to the test." },
                new MovieViewModel { Id = 5, Name = "5 Centimeters Per Second", Rating = "8", ReleaseYear = 2007, Genre = "Romance/Drama", Form = "Animation",  Poster = "~/imgs/5Centimeter.png", Trailer = "https://www.youtube.com/embed/wdM7athAem0?si=z4e4W5dKvbMIzoJg", Synopsis = "What happens when two people love each other but just aren't meant to be together? Takaki Toono and Akari Shinohara are childhood friends, but circumstances beyond their control tear them apart. They promise to stay in contact, and although the progression of time widens the distance between them, the chain of memories remains ever-present.\r\n\r\nByousoku 5 Centimeter is a romantic drama that focuses on the mundane and harsh reality of long-distance relationships. Stuck in the past and unable to make any new memories, Takaki and Akari cling to the hope of seeing each other again. They live their everyday lives half-heartedly, both hurting themselves and the people around them." },
                new MovieViewModel { Id = 6, Name = "Hello World", Rating = "10", ReleaseYear = 2019, Genre = "Romance/Drama", Form = "Animation", Poster = "~/imgs/HelloWorld.png", Trailer = "https://www.youtube.com/embed/shoWFRnNoWw?si=8R2jg-N4LvmQFgwU", Synopsis = "The year is 2027, and the city of Kyoto has undergone tremendous technological advancement. Within the city live two classmates: Naomi Katagaki, a socially awkward and introverted boy; and Ruri Ichigyou, a girl with a cold personality who is often blunt with people. Despite sharing Ruri's love of reading, Naomi is afraid to approach her due to her unfriendly nature.\r\n\r\nOne day, while out on a walk, Naomi witnesses a crimson aurora pierce through the sky for a brief moment before vanishing. Shortly after, he encounters a three-legged crow and a mysterious hooded man, who reveals himself to be Naomi from 10 years in the future, explaining that he has come to change an imminent tragic event that happens to Ruri soon after they start dating. Initially taking his words with a grain of salt, present-day Naomi follows his future self's instructions and starts getting closer to Ruri, determined to save her. Will he be able to change the future?" },
                new MovieViewModel { Id = 7, Name = "A Silent Voice", Rating = "10", ReleaseYear = 2016, Genre = "Romance/Drama", Form = "Animation", Poster = "~/imgs/ASilentVoice.png", Trailer = "https://www.youtube.com/embed/nfK6UgLra7g?si=H_vJTjmier6gxZja", Synopsis = "As a wild youth, elementary school student Shouya Ishida sought to beat boredom in the cruelest ways. When the deaf Shouko Nishimiya transfers into his class, Shouya and the rest of his class thoughtlessly bully her for fun. However, when her mother notifies the school, he is singled out and blamed for everything done to her. With Shouko transferring out of the school, Shouya is left at the mercy of his classmates. He is heartlessly ostracized all throughout elementary and middle school, while teachers turn a blind eye.\r\n\r\nNow in his third year of high school, Shouya is still plagued by his wrongdoings as a young boy. Sincerely regretting his past actions, he sets out on a journey of redemption: to meet Shouko once more and make amends.\r\n\r\nKoe no Katachi tells the heartwarming tale of Shouya's reunion with Shouko and his honest attempts to redeem himself, all while being continually haunted by the shadows of his past." },
                new MovieViewModel { Id = 8, Name = "Patema Inverted", Rating = "8", ReleaseYear = 2013, Genre = "Action/Drama", Form = "Animation", Poster = "~/imgs/Patema.png", Trailer = "https://www.youtube.com/embed/Aa7sa-Zd-3E?si=h1URL5LX95UZko6U", Synopsis = "Patema is a plucky young girl from an underground civilization boasting an incredible network of tunnels. Inspired by a friend that mysteriously went missing, she is often reprimanded due to her constant excursions of these tunnels due to her royal status. After she enters what is known as the \"forbidden zone,\" she accidentally falls into a giant bottomless pit after being startled by a strange creature.\r\n\r\nFinding herself on the surface, a world literally turned upside down, she begins falling towards the sky only to be saved by Age, a discontented student of the totalitarian nation known as Aiga. The people of Aiga are taught to believe that \"Inverts,\" like Patema, are sinners that will be \"swallowed by the sky,\" but Age has resisted this propaganda and decides to protect his new friend. A chance meeting between two curious teenagers leads to an exploration of two unique worlds as they begin working together to unveil the secrets of their origins in Sakasama no Patema, a heart-warming film about overcoming differences in order to coexist." },
                new MovieViewModel { Id = 9, Name = "Into the Forest of Fireflies' Light", Rating = "7", ReleaseYear = 2011, Genre = "Drama", Form = "Animation", Poster = "~/imgs/Hotarubi.png", Trailer = "https://www.youtube.com/embed/m-UdkLhtPeM?si=HJvmofWalK_mFOk8", Synopsis = "During a summer vacation at her grandfather’s house, six-year-old Hotaru Takegawa gets lost in a forest rumored to be inhabited by spirits. While crying out in desperation, Hotaru is approached by Gin—a mysterious boy wearing a mask—who offers to help her.\r\n\r\nOverjoyed at the sight of another person, Hotaru runs to Gin with open arms only to be rudely fended off. However, she quickly learns the grave reason behind his behavior: a dreadful curse has been cast upon Gin. Should he ever be touched by a human being, he will disappear forever.\r\n\r\nThough Gin urges her to never return, Hotaru does the exact opposite, and before too long, the two become close friends despite his delicate situation. Nonetheless, as years pass and their mutual feelings grow stronger, Hotaru and Gin start struggling with the boundaries that destiny has set between them." },
                new MovieViewModel { Id = 10, Name = "Princess Mononoke", Rating = "8", ReleaseYear = 1997, Genre = "Action/Drama", Form = "Animation", Poster = "~/imgs/Mononoke.png", Trailer = "https://www.youtube.com/embed/4OiMOHRDs14?si=tjNITQn7rgwRrhWU", Synopsis = "When an Emishi village is attacked by a fierce demon boar, the young prince Ashitaka puts his life at stake to defend his tribe. With its dying breath, the beast curses the prince's arm, granting him demonic powers while gradually siphoning his life away. Instructed by the village elders to travel westward for a cure, Ashitaka arrives at Tatara, the Iron Town, where he finds himself embroiled in a fierce conflict: Lady Eboshi of Tatara, promoting constant deforestation, stands against Princess San and the sacred spirits of the forest, who are furious at the destruction brought by the humans. As the opposing forces of nature and mankind begin to clash in a desperate struggle for survival, Ashitaka attempts to seek harmony between the two, all the while battling the latent demon inside of him. Princess Mononoke is a tale depicting the connection of technology and nature, while showing the path to harmony that could be achieved by mutual acceptance." },
                new MovieViewModel { Id = 11, Name = "Maquia: When the Promised Flower Blooms", Rating = "10", ReleaseYear = 2018, Genre = "Drama", Form = "Animation", Poster = "~/imgs/Maquia.png", Trailer = "https://www.youtube.com/embed/AEWvRqZQ0RU?si=NPfv-uGlAg3TITJB", Synopsis = "Maquia is a member of a special race called the Iorph—mystical beings who can live for hundreds of years and remain separate from the lives and daily troubles of mankind. However, Maquia has always felt lonely despite being surrounded by her people, as she was orphaned from a young age. She daydreams about the outside world, but dares not travel from her home due to the warnings of the clan's chief.\r\n\r\nOne day however, the outside world finds her, as the power-hungry kingdom of Mezarte invades her homeland. They already have what is left of the giant dragons, the Renato, under their control, and now their king wishes to add the immortality of the Iorph to his bloodline.\r\n\r\nThe humans and their Renato ravage the Iorph homeland and kill most of its inhabitants. Caught in the midst of the attack, Maquia is carried off by one of the Renato that has gone berserk. It soon dies, and she is left deserted in a forest far from home, now truly alone save for the cries of a single baby off in the distance. Maquia finds the baby in a destroyed village and decides to raise him as her own, naming him Ariel. Although she knows nothing of the human world, how to raise a child that ages much faster than her, or how to live with the smoldering loneliness inside, she is determined to make it all work somehow." },
                new MovieViewModel { Id = 12, Name = "I Want to Eat Your Pancreas", Rating = "9", ReleaseYear = 2018, Genre = "Romance/Drama", Form = "Animation", Poster = "~/imgs/Pancreas.png", Trailer = "https://www.youtube.com/embed/MmoBvmJA9XI?si=wmoDFfW87jTakDuL", Synopsis = "The aloof protagonist: a bookworm who is deeply detached from the world he resides in. He has no interest in others and is firmly convinced that nobody has any interest in him either. His story begins when he stumbles across a handwritten book, titled Living with Dying. He soon identifies it as a secret diary belonging to his popular, bubbly classmate Sakura Yamauchi. She then confides in him about the pancreatic disease she is suffering from and that her time left is finite. Only her family knows about her terminal illness; not even her best friends are aware. Despite this revelation, he shows zero sympathy for her plight, but caught in the waves of Sakura's persistent buoyancy, he eventually concedes to accompanying her for her remaining days.\r\n\r\nAs the pair of polar opposites interact, their connection strengthens, interweaving through their choices made with each passing day. Her apparent nonchalance and unpredictability disrupts the protagonist's impassive flow of life, gradually opening his heart as he discovers and embraces the true meaning of living." },
                new MovieViewModel { Id = 13, Name = "Eternal Sunshine of the Spotless Mind", Rating = "8", ReleaseYear = 2004, Genre = "Romance/Drama", Poster = "~/imgs/EternalSunshine.png", Trailer = "https://www.youtube.com/embed/07-QBnEkgXU?si=W_7PtOBTzN3d8wWh", Synopsis = "Joel Barish (Jim Carrey) is an emotionally withdrawn man and Clementine Kruczynski (Kate Winslet) is his girlfriend who is a dysfunctional free spirit. They are inexplicably attracted to each other despite their different personalities.\r\n\r\nThey do not realize it at the time, but they are former lovers now separated after two years together. After a nasty fight, Clementine has had her memories of their relationship erased from her mind. Upon learning this, Joel is devastated and goes to the doctor to have the same procedure done. However, while unconscious, Joel has second thoughts and decides he wants to keep his memories of Clementine.\r\n\r\nMuch of the film takes place in Joel's brain as he tries to find a way to preserve his memories of Clementine, and two Lacuna techies Patrick (Elijah Wood) and Stan (Mark Ruffalo) try to erase the memories. We watch their love and courtship go in reverse: The memories are slowly erased while Joel tries his best to resist the procedure and hide inside his mind.\r\n" },
                new MovieViewModel { Id = 14, Name = "The Truman Show", Rating = "8", ReleaseYear = 1998, Genre = "Comedy/Drama", Poster = "~/imgs/Truman.png", Trailer = "https://www.youtube.com/embed/dlnmQbPGuls?si=KTmhkoxuxupqkvFA", Synopsis = "Truman Burbank (Jim Carrey) is an insurance salesman leading an idyllic, peaceful life on Seahaven Island.\r\n\r\nTruman also happens to be the star of the most popular live show in television history. The only problem is, he doesn't know it! In fact, the entire island of Seahaven is a complete fabrication; a massive set surrounded by a protective dome that produces the most sophisticated effects and imagery to mimic the sky, weather, and temperature of the real world. Set just off of what appears to be the coast of Florida, the dome is run by a massive staff hidden just beyond its barriers and managed by the artistically eccentric Director, Christof (Ed Harris). Christof has filmed Truman since just before his birth and sees the project as a ray of hope; a way to truthfully portray a person's life nonstop while maintaining their innocence." },
                new MovieViewModel { Id = 15, Name = "Seeking a Friend for the End of the World", Rating = "8", ReleaseYear = 2012, Genre = "Comedy/Drama", Poster = "~/imgs/Seeking.png", Trailer = "https://www.youtube.com/embed/EmXWcXKXWBk?si=gs2rrxXfYIyRpc06", Synopsis = "A broadcast announces that a seventy mile wide asteroid nicknamed 'Matilda' is on a collision course with Earth and that a last-ditch effort to destroy it has failed. The world has three weeks until impact, at which time all of humanity will be wiped out. Dodge Petersen (Steve Carell) and his wife, Linda (Nancy Carell), listen to the announcement (voice of Brad Morris) from their car radio at the side of the road. Seconds later, and without saying a word to him, Linda flees the vehicle.\r\n\r\nThe next day, Dodge returns to a near empty workplace where he sells insurance. While everyone around him has reacted differently to the impending doom by either indulging in drugs or engaging in guilt-free sex, Dodge's life remains the same. Even the absence of his wife cannot shake the stagnant predictability of it. His housekeeper, Elsa (Tonita Castro), continues to show up at his apartment to clean despite Dodge's insistence that there is no further need to do so.\r\n\r\nAfter attending a friend's party, Dodge returns home and reminisces about his high school sweetheart, Olivia. He then notices his neighbor Penny (Keira Knightley) crying on the fire escape. She admits to him that she has just ended her relationship with her boyfriend Owen (Adam Brody) because he caused her to miss the last flight to England to see her family. Returning to her apartment, Penny gives Dodge a handful of mail she received for him but kept forgetting to deliver - indirectly telling him that Linda was having an affair." },
                new MovieViewModel { Id = 16, Name = "La La Land", Rating = "8", ReleaseYear = 2016, Genre = "Comedy/Drama/Musical", Poster = "~/imgs/LaLaLand.png", Trailer = "https://www.youtube.com/embed/0pdqf4P9MB8?si=UwMuQv3rzgHFqRQy", Synopsis = "While stuck in Los Angeles traffic, Sebastian \"Seb\" Wilder, a jazz pianist, has a moment of road rage directed at aspiring actress Mia Dolan. After a hard day at work, Mia's subsequent audition goes awry when the casting director takes a phone call during an emotional scene. That night, her roommates take her to a lavish party in the Hollywood Hills, promising her that someone in the crowd could jump-start her career. After her car is towed, she walks home in disappointment.\r\n\r\nDuring a gig at a restaurant, Seb slips into jazz improvisation, despite the owner's warning to only play traditional Christmas pieces. Mia overhears him playing as she passes by. Moved, she enters the restaurant and observes Seb being fired for his disobedience. As he storms out, Mia attempts to compliment him, but he brushes her off. Months later, she runs into Seb at a party where he plays in a 1980s pop cover band. After the gig, they walk to their cars and lament wasting a lovely night together despite their clear chemistry." },
                new MovieViewModel { Id = 17, Name = "Her", Rating = "8", ReleaseYear = 2013, Genre = "Romance/Drama", Poster = "~/imgs/Her.png", Trailer = "https://www.youtube.com/embed/dJTU48_yghs?si=U8Zya3rY1tc4tpkx", Synopsis = "Theodore Twombly (Joaquin Phoenix) is a lonely, introverted man who writes personal letters for people with difficulties expressing their feelings. He is very good at his job at handwrittenletters.com.\r\n\r\nUnhappy because of his impending divorce from his childhood sweetheart Catherine (Rooney Mara), Theodore purchases a talking operating system with artificial intelligence, designed to adapt and evolve like a human being. He decides he wants the OS to have a female identity, and she names herself \"Samantha\" (Scarlett Johansson).\r\n\r\nTheodore is fascinated by her ability to learn and grow psychologically, and they bond over their discussions about love and life, during which Theodore explains he is avoiding signing his divorce papers, because of his reluctance to let go of Catherine.\r\n\r\nTheodore is convinced by Samantha to go on a blind date with an acquaintance (Olivia Wilde) that his longtime friend, Amy (Amy Adams) has been trying to set him up with. To his surprise, he and the woman hit it off. As they are about to have sex, the woman asks if Theodore is willing to commit to her, and when he hesitates, she walks out on him." },
                new MovieViewModel { Id = 18, Name = "Inception", Rating = "9", ReleaseYear = 2010, Genre = "Action", Poster = "~/imgs/Inception.png", Trailer = "https://www.youtube.com/embed/YoHD9XEInc0?si=XQn_ii7RYCtsqLeW", Synopsis = "A young man, exhausted and delirious, washes up on a beach, looking up momentarily to see two young children (Claire Geare and Magnus Nolan) playing in the sand before he passes out. An armed guard (Tohoru Masamune) discovers him and has him brought to a large, seaside palace where the proprietor, an elderly Japanese man, is told of the stranger's arrival. The only objects found on him were a handgun and a brass top. The old man allows the stranger entry. He is dragged in and given some food which he struggles to eat as the old man picks up the brass top and says, \"You remind me of someone...a man I met in a half remembered dream. He was possessed of some radical notions.\" The stranger looks up in realization as the scene shifts...\r\n\r\nDom Cobb (Leonardo DiCaprio) and his partner, Arthur (Joseph Gordon-Levitt), speak to a prospective client, Saito (Ken Watanabe), in an eerily similar dining room about the services they provide; specifically protection against thieves specialized in extracting valuable information from a subject while they're dreaming. Cobb explains that, when one is asleep, one's mind is vulnerable to attack and what he can do is train Saito's mind to subconsciously defend against extractors to protect whatever secrets he may be hiding. He backs up his claim by revealing that he is the most skilled extractor there is and knows all the tricks involved. Appearing skeptical, Saito stands to leave, telling Cobb he will consider his proposition, and exits the room to join a small party in the main hall." },
                new MovieViewModel { Id = 19, Name = "Predestination", Rating = "8", ReleaseYear = 2014, Genre = "Action/Drama", Poster = "~/imgs/Predestination.png", Trailer = "https://www.youtube.com/embed/-FcK_UiVV40?si=1P3_J79Ji3PqUE94", Synopsis = "The film begins with a temporal agent attempting to apprehend an infamous terrorist known as the 'Fizzle Bomber', who planted a bomb in a building attempting to kill hundreds of people. The agent succeeds in containing the bomb but is severely injured when his face is partially exposed to the resulting explosion. The Fizzle Bomber manages to escape and the temporal agent returns to his time-line (in the future) using a coordinate transformer field kit (a time machine) with the help of an unknown person.\r\n\r\nReturning to the future, in 1992, the agent awakens to find that he has undergone facial surgery to save his life. Doctors grafted on new skin and reconstructed the agent's face and throat as they were badly damaged. It is revealed during this time that the agent works for a mysterious organization, known as the Temporal Bureau, that sends temporal agents through time to prevent major crimes that would cost the lives of thousands of people and that the Fizzle Bomber is the only criminal to elude them thus far; the Fizzle Bomber is also responsible for killing 11,000 people in New York in March1975." },
                new MovieViewModel { Id = 20, Name = "Hereditary", Rating = "8", ReleaseYear = 2018, Genre = "Horror/Drama/Mystery", Poster = "~/imgs/Hereditary.png", Trailer = "https://www.youtube.com/embed/V6wWKNij_1M?si=QkNOWEKhzgB4by9K", Synopsis = "The story begins with the viewer looking out from a window in a workshop to a tree house, then turning and zooming in to a bedroom in a dollhouse that is in the workshop. Steve Graham wakes his teenage son Peter and 13-year-old daughter Charlie for their 78-year-old grandmother Ellen Taper Leigh's funeral. Steve finds Charlie sleeping in the tree house.\r\n\r\nSteve's wife Annie, an artist who sculpts miniature dioramas, delivers the eulogy at her mother's service. Charlie makes a clucking noise while drawing a strange sketch during the speech.\r\n\r\nAnnie talks to Charlie about Ellen at bedtime that night. Charlie claims that her grandmother always wished Charlie were a boy. To Annie's confusion, Charlie also wonders aloud who will care for her now that Ellen is dead. Annie later sees a haunting vision of Ellen after looking through a memory book while in Annie's workshop.\r\n" },
                new MovieViewModel { Id = 21, Name = "Midsommar", Rating = "8", ReleaseYear = 2019, Genre = "Horror/Drama/Mystery", Poster = "~/imgs/Midsommar.png", Trailer = "https://www.youtube.com/embed/1Vnghdsjmd0?si=ii4e-65E-sH0j87K", Synopsis = "The film opens with a mural of a bizarre, eerie ritual taking place. We then see images of dark, snowy forests with the sound of old folk singing playing in the background.\r\n\r\nCollege student Dani Ardor (Florence Pugh) calls her parents but is sent to voicemail. She expresses concern over her bipolar sister Terri (Klauda Csanyi), who left her a cryptic message recently. Dani then calls her boyfriend Christian Hughes (Jack Reynor), who is hanging out with his buddies Josh (William Jackson Harper), Mark (Will Poulter), and Pelle (Vilhelm Blomgren). Christian assures Dani that this is just another one of Terri's episodes, but he adds that Dani only feeds into Terri's antics. After he hangs up, she gets a call from an unknown number. Josh and Mark think Christian should just end it with Dani since it's clear he's wanted out of the relationship for a while, and as they are planning an upcoming trip to Sweden (Eastern Europe), Pelle suggests they will meet lots of other women. Dani calls Christian again and is wailing hysterically. We then see authorities going into Dani's parents' home, where Terri has flooded the house with carbon monoxide, killing her parents before stuffing the tube into her mouth and taping it there. Christian goes to Dani's apartment to console her." },
                new MovieViewModel { Id = 22, Name = "Talk to Me", Rating = "7", ReleaseYear = 2022, Genre = "Horror/Thriller", Poster = "~/imgs/TalkToMe.png", Trailer = "https://www.youtube.com/embed/aLAKJu9aJys?si=moQqNxwBYATYXuOD", Synopsis = "A young man, Cole, stumbles through a crowded house party as he attempts to locate his brother Duckett, who has locked himself in a bedroom. Cole breaks down the door and retrieves an injured Duckett, before taking him into the living room where Duckett stabs his brother in the chest and then himself in the face, killing himself in front of the party-goers.\r\n\r\nMeanwhile, 17-year-old Mia is struggling with the second anniversary of her mother Rhea's suicide and her distant relationship with her father, Max. Mia, her best friend Jade and Jade's little brother Riley sneak out to a house party hosted by Hayley and Joss where the main attraction is the severed, embalmed hand of a powerful medium, which they use to conjure spirits by lighting a candle and saying the phrase \"talk to me,\" before proceeding to say \"I let you in\" for full possession, and blowing out the candle once ninety seconds has passed. Mia volunteers to go first, and is possessed by a spirit that threatens Riley before Joss and Hayley rip the hand away and blow out the candle, bringing Mia back to her normal self." },
                new MovieViewModel { Id = 23, Name = "Everything Everywhere All at Once", Rating = "9", ReleaseYear = 2022, Genre = "Action/Comedy", Poster = "~/imgs/EEAO.png", Trailer = "https://www.youtube.com/embed/wxN1T1uxQ2g?si=UunHF8XF9vq6j20Z", Synopsis = "With her laundromat teetering on the brink of failure and her marriage to wimpy husband Waymond on the rocks, overworked Evelyn Wang struggles to cope with everything, including tattered relationships with her judgmental father Gong Gong and her daughter Joy. She must also brace herself for an unpleasant meeting with an impersonal bureaucrat: Deirdre, the shabbily-dressed IRS auditor. However, as the stern agent loses patience, an inexplicable multiverse rift becomes an eye-opening exploration of parallel realities. Will Evelyn jump down the rabbit hole? How many stars are in the universe? Can weary Evelyn fathom the irrepressible force of possibilities, tap into newfound powers, and prevent an evil entity from destroying the thin, countless layers of the unseen world?" },
                new MovieViewModel { Id = 24, Name = "Beau Is Afraid", Rating = "7", ReleaseYear = 2023, Genre = "Comedy/Horror/Drama", Poster = "~/imgs/Beau.png", Trailer = "https://www.youtube.com/embed/XrCg9G_OHAA?si=qj0MuS9T-IiQEPwi", Synopsis = "With his mother's gloomy spectre looming over his entire life, grizzled outcast Beau has never made sense of his miserable existence. And perpetually baffled by reality and his conflicting urgent needs, the neurotic New Yorker gets a hearty slice of crippling anxiety and sheer paranoia when he sets foot in a strangely familiar war zone: his neighbourhood. But as the pill-popping hermit reluctantly embarks on a Sisyphean quest for answers, determined to reunite with his estranged mum at all costs, Beau must summon every last ounce of courage to confront everything that has kept him in the dark. To have a fighting chance of deciphering the sinister secrets that continue to rule his life, Beau must grapple with deep-seated phobias, deal with heavy guilt, and face the menacing parent. Because, in his case, love was a fiendish trap--the labyrinthine depths of maternal affection were only the means to an end. Now, the drugs don't work, and Beau is afraid. As the universe conspires against his conquest and the end draws near, will Beau be able to fight his inner demons for a change?" },
